<template>
  <div id="container">
    <ion-card @click="openModal(data)">
      <ion-card-header>
         <ion-img src="data:image/jpeg;base64,"></ion-img>
        <ion-card-subtitle>{{data.marca}}</ion-card-subtitle>
        <ion-card-title v-if="parseInt(data.quantidade) > 1">Pack {{data.quantidade}} x {{data.tamanho}}</ion-card-title>
        <ion-card-title v-if="data.quantidade == 1">{{data.quantidade}}x{{data.tamanho}}</ion-card-title>
      </ion-card-header>

      <ion-card-content>
        <p><b>Estilo:</b> {{data.estilo}}</p>
        <p><b>Álcool:</b> {{data.alcool}}</p>
        <p><b>Estilo:</b> {{data.estilo}}</p>
        <p><b>Estilo:</b> {{data.estilo}}</p>
      </ion-card-content>
    </ion-card>
    
  </div>
</template>

<script>
import { defineComponent } from "vue";
import BeerView from "./BeerView.vue"
import {
  IonCard,
  IonCardContent,
  IonCardHeader,
  IonCardTitle,
  IonCardSubtitle,
  IonImg,
  modalController
} from "@ionic/vue";

export default defineComponent({
  name: "BeerCard",
  components: {
    IonCard,
    IonCardContent,
    IonCardHeader,
    IonCardTitle,
    IonCardSubtitle,
    IonImg
  },
  props: {
    data: Object,
  },
  methods: {
    async openModal(beer) {
      console.log(beer);
      const modal = await modalController
        .create({
          component: BeerView,
          initialBreakpoint: 0.5,
          breakpoints: [0, 0.25, 0.5, 0.75, 1],
          componentProps: {
            title: beer.marca,
            desc: beer.desc
          },
        })
      return modal.present();
    },
  }
});
</script>

